"use strict";let tabsWrap=document.querySelector(".tabs__list");tabsWrap.addEventListener("click",function(t){if("tabs__item"==t.target.className){let e=t.target.getAttribute("data-tab"),a=document.getElementsByClassName("tabs__item");for(let t=0;t<a.length;t++)a[t].classList.remove("active");t.target.classList.add("active");let s=document.getElementsByClassName("tabs__content");for(let t=0;t<s.length;t++)e==t?s[t].classList.add("active"):s[t].classList.remove("active")}});